function hasClass(s,a){return new RegExp(" "+a+" ").test(" "+s.className+" ")}function addClass(s,a){hasClass(s,a)||(s.className+=" "+a)}function removeClass(s,a){var e=" "+s.className.replace(/[\t\r\n]/g," ")+" ";if(hasClass(s,a)){for(;e.indexOf(" "+a+" ")>=0;)e=e.replace(" "+a+" "," ");s.className=e.replace(/^\s+|\s+$/g,"")}}function toggleClass(s,a){var e=" "+s.className.replace(/[\t\r\n]/g," ")+" ";if(hasClass(s,a)){for(;e.indexOf(" "+a+" ")>=0;)e=e.replace(" "+a+" "," ");s.className=e.replace(/^\s+|\s+$/g,"")}else s.className+=" "+a}var theToggle=document.getElementById("toggle");theToggle.onclick=function(){return $("#toggle").hasClass("")&&$(".main-img").removeClass("main-img").addClass("main-slider"),$("#toggle").hasClass("on")&&$(".main-slider").removeClass("main-slider").addClass("main-img main-img-after"),toggleClass(this,"on"),!1};